import React from 'react';
import '../style.css';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';

class ScreenPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            images: []
        }
    }

    componentDidMount() {        
        const params = this.props.match.params;
        axios.get(`https://wild-games.herokuapp.com/api/v1/${Number(params.idaccomodation)}`)
        .then(response => response.data)
        .then(data => {
            this.setState({ images: data[0] });
        })     
    }

    render() {
        return (
            <div className="container ButtonsFilter mt-5">
                <Link to="/"><Button variant="dark">Retour</Button></Link>
            </div>

        )
    }
}

export default ScreenPage;
