import React from 'react';
import './Game.css';
import { Link } from 'react-router-dom';

const Game = ({game, deleteGame}) => {

    return(
        <div className="GameContainer">

            <Link to={{ pathname: `/jeu/screenshots/${game.id}`, query: {game}}} >
                <h1>{game.name}</h1>
            </Link>

            <small>released : {game.released}</small>
                <p><strong>Rating :</strong> {game.rating}</p>
                <p><strong>Genre :</strong></p>
    {game.genres.map((genre, index) => <li key={index}>{genre.name}</li>)}
                <button className="Delete" onClick={() => deleteGame(game.name)}>Delete it</button>
                <br/>
            <img src={game.background_image} alt={game.name}/>
        </div>
    );
}

export default Game;
