import React from 'react';
import axios from 'axios';
import Game from './Game';

class GameList extends React.Component {

    state = {
        betterGamesOnly: false,
        games: []

    }
    componentDidMount(){
        axios.get(`https://wild-games.herokuapp.com/api/v1`)
        .then(response => response.data)
        .then(data =>this.setState({games: data}))

    }

    deleteGame = (name) => {
        const {games} = this.state;
        const  newArrayGame = games.filter(element => element.name !==name);
        this.setState({games: newArrayGame})

    }
    handleShowGameFive= ()=> {
        this.setState({betterGamesOnly: !this.state.betterGamesOnly});
    }

    render(){
        const {games}= this.state;

        return(

            <div className='GameList'>
               <div>Page list of games</div> 
                <button onClick={this.handleShowGameFive}>
                  {this.state.betterGamesOnly? 'ALL GAMES': "BEST GAMES"}
               </button>
               {games
            .filter(game => {
                return (
                    !this.state.betterGamesOnly || game.rating >= 4.5
                    );
                })
            .map(game=> <Game game={game} deleteGame= {this.deleteGame} />)
          } 

            </div>
        )
    }

}

export default GameList;
