import React from 'react';
import '../style.css';
import Modal from 'react-bootstrap/Modal';

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            games: [],
            show: false,
        }
    }

    handleClose = () => {
        this.setState({ show: false });
    }

    handleShow = () => {
        this.setState({ show: true });
    }

    // refreshPage = () => {
    //     window.location.reload(false);
    // }

    render() {
        return (
            <>
            <Modal size="lg" show={this.state.show} onHide={this.handleClose}>
                <Modal.Header closeButton>
                <Modal.Title>Supprimer le jeu {this.props.name}</Modal.Title>
                </Modal.Header>
                <Modal.Body>Êtes-vous sûr de vouloir supprimer ce jeu de la liste ?</Modal.Body>
                <Modal.Footer>
                <button onClick={this.handleClose}>
                    Annuler
                </button>
                <button  onClick={() => {this.deleteGame(this.props.id);this.handleClose()}}>
                    Supprimer
                </button>
                </Modal.Footer>
            </Modal>
            <div className="container">
                <div className="Card">
                    <div className="InfoUp">
                        <div className="col-md-1">
                            <img src={this.props.background_image} className="img-round" alt={this.props.name}/>
                        </div>
                        <div className="col-md-7">
                            <div>
                                <h3>{this.props.name} <span className="IdItem">{`(id : ${this.props.id})`}</span></h3>
                            </div>
                            <div>
                                <p>Genre : , {this.props.released}</p>
                            </div>
                        </div>
                        <div className="buttons col-md-4">
                            <button onClick={this.handleShow}> 
                                Supprimer
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            </>
        )
    }
}

export default Game;
