import React from 'react';
import styles from './Games.module.css'
import { Link } from 'react-router-dom'

const Game = (props) => {

    let { genres } = props;
    let { screenshots} = props
    console.log(props)

    genres = genres.join(', ')

    return (
        <div className={styles.GameContainer}>
            <p>{props.name}</p>
            <img src={props.image} alt={props.name} />
            <Link to={{pathname: `/jeu/screenshots/${props.id}`, query : screenshots}}>{props.name}'s screenshots</Link> 
            <p>Rating : {props.rating} / 5</p>
            <p>Genres : {genres}</p>
            <button onClick={()=>props.deleteHandler(props.name)}>Delete</button>
        </div>
    )
}

export default Game;
