import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Game from './Game';
import './GameList.css'

const Gamelist = () => {

const [GameList, SetGameList] = useState([])
const [BestGame, SetBestGame] = useState(false)
const [Year, setYear] = useState('')

useEffect(() => {
    const url = 'https://wild-games.herokuapp.com/api/v1'
    axios.get(url)
    .then(response => response.data)
    .then(data => SetGameList(data))
    .catch(error => console.log(error))

}, []);

const deleteGame = (name) => {
    // console.log(name)
    const deleteGameArray = GameList.filter(game => game.name !== name)
    SetGameList(deleteGameArray)
};

const filterGame = () => {
    SetBestGame(!BestGame)
}

const pickerYear = (e) => {
    setYear(e.target.value)
}

const FilterYear = () => {
   const newArray = GameList.filter(element => element.released.includes(Year))
   SetGameList(newArray)
}

const resetFilter = () => {
    const url = 'https://wild-games.herokuapp.com/api/v1'
    axios.get(url)
    .then(response => response.data)
    .then(data => SetGameList(data))
    .catch(error => console.log(error))
}
    return(
        <div className="GameListContainer">
            <h1>Game List App</h1>
            <button className="filterBtn" onClick={filterGame}>
            {BestGame ? 'All Games' : 'Best Games ( rating > 4.4 )'}
            </button>
            <label>filter by genre : </label>
            <select name="genre" onChange={pickerYear}>
                <option value="">Please choose a year</option>
                <option value="2011">2011</option>
                <option value="2015">2015</option>
                <option value="2013">2013</option>
                <option value="2012">2012</option>
                <option value="2007">2007</option>
            </select>
            <button className="filterBtnYear" onClick={FilterYear}>filter by year</button>
            <button className="filterBtn" onClick={resetFilter}>Reset Filter</button>
                <div className="GameListResult">
                    {GameList.filter(rating => !BestGame || rating.rating >= 4.4)
                    .map((game, index) => 
                    <Game 
                    key = {index}
                    game = {game}
                    deleteGame = {deleteGame}
                    />
                        )}
                </div>
        </div>
    );
}

export default Gamelist;
