import React from 'react';
import GameScreen from './GameScreen';
import './GameDetails.css'
import { Link } from 'react-router-dom';

const GameDetails = (props) => {

    const game = props.location.query.game
    return(
        <div>
            <h1>{game.name}</h1>
            <small>released : {game.released}</small>
                <p>Rating : {game.rating}</p>
                <h2>Game's Screenshot</h2>
                <Link to="/"><button className="back">Back to games</button></Link>
            <div className="GameScreen">
                <p>Clip Video :</p>
                <iframe src={game.clip.clip} title={game.name}/>
                {game.short_screenshots.map((element, index) =>
                    <GameScreen
                    key= {index} 
                    image={element.image}
                    id={element.id}
                    />
                    )}
            </div>
        </div>
    );
}

export default GameDetails;
