import React from 'react';
import './Game.css';
import {NavLink} from 'react-router-dom';

const Game = ({game, deleteGame}) => {

    return(
        <div className= 'Game'>
        <img src={game.background_image} alt='img-game' className= 'img-game'/>
        <h2>{game.name }</h2>
        <h4> note: {game.rating}</h4>
        <button  className= 'delete-button' onClick={() => deleteGame(game.name)}>Supprimer</button>
        {/* <NavLink activeClassName="active-link" id="screenshot" to='/jeu/screenshot/:id'>{game.short_screenshots}</NavLink> */}

        </div>
    )
}

export default Game;
